function parseInput(input) {
  const monkeysNumbers = new Map();
  const monkeysOperators = new Map();
  for (const line of input.split('\n')) {
    const id = line.split(':')[0];
    const hasNumber = line.match(/\d+/);
    if (hasNumber) {
      const value = hasNumber[0];
      monkeysNumbers.set(id, Number(value));
      continue;
    }
    const [operand1, operator, operand2] = line.split(': ')[1].split(' ');
    monkeysOperators.set(id, {operand1, operator, operand2});
  }
  return {monkeysNumbers, monkeysOperators};
}

function doOperation(operand1, operator, operand2) {
  switch (operator) {
    case '+': return operand1 + operand2;
    case '-': return operand1 - operand2;
    case '*': return operand1 * operand2;
    case '/': return operand1 / operand2;
  }
}

function doSomeOperations(monkeysNumbers, monkeysOperators) {
  for (const [id, op] of monkeysOperators.entries()) {
    if (monkeysNumbers.has(op.operand1)) {
      op.operand1 = monkeysNumbers.get(op.operand1);
    }
    if (monkeysNumbers.has(op.operand2)) {
      op.operand2 = monkeysNumbers.get(op.operand2);
    }
    if (typeof op.operand1 === 'number' && typeof op.operand2 === 'number') {
      const result = doOperation(op.operand1, op.operator, op.operand2);
      monkeysNumbers.set(id, result);
      monkeysOperators.delete(id);
    }
  }
}


// Part 1
const {monkeysNumbers, monkeysOperators} =parseInput(input);
while (monkeysOperators.size > 0) doSomeOperations(monkeysNumbers, monkeysOperators);
console.log(monkeysNumbers.get('root'));

