const {monkeysNumbers: monkeysNbs, monkeysOperators: monkeysOp} = parseInput(input);
monkeysNbs.set('humn', 'x');
monkeysOp.get('root').operator = '=';

let lastSize = Infinity;
while (monkeysOp.size != lastSize) {
  lastSize = monkeysOp.size
  doSomeOperations(monkeysNbs, monkeysOp);
}

function doReverseOperation(operand1, operator, operand2) {
  switch (operator) {
    case '+': return operand1 - operand2;
    case '-': return operand1 + operand2;
    case '*': return operand1 / operand2;
    case '/': return operand1 * operand2;
  }
}

const root = monkeysOp.get('root');

let result = typeof root.operand1 === 'number' ? root.operand1 : root.operand2;
let currentId = typeof root.operand1 === 'number' ? root.operand2 : root.operand1;

while ( currentId != 'x') {
  const op = monkeysOp.get(currentId);
  if (op.operator == '+' || op.operator == '*') {
    const operand = typeof op.operand1 === 'number' ? op.operand1 : op.operand2;
    result = doReverseOperation(result, op.operator, operand);
  } else {
    if (typeof op.operand1 === 'number') {
      result = doOperation(op.operand1, op.operator, result);
    } else {
      result = doReverseOperation(op.operand2, op.operator, result)
    }
  }
  currentId = typeof op.operand1 === 'number' ? op.operand2 : op.operand1
}

